spdm=30;
pc=lam0/500;
for lam=0.5*lam0:pc:1.5*lam0;
    D=5/lam/k;
    x=D:D:5/lam;
    y=x-D/2;
    n=zeros(1,k);
    
    for i=1:N
        if E(i)<5/lam
            j=ceil(E(i)/D);
            n(j)=n(j)+1;
        end;
    end;
    
    fe=n/N/D;
    f=lam*exp(-lam*y);
    spd=sum((fe-f).^2);
    if spd<spdm
        lamF=lam;
        spdm=spd;
    end;
    
end;
    
